example:
/* Custom variables */
$primary: #007bff;
$secondary: #6c757d;
$success: #28a745;

/* Custom component styles */
.my-custom-button {
  @extend .btn;
  background-color: $primary;
  border-color: $primary;

  &:hover {
    background-color: darken($primary, 10%);
    border-color: darken($primary, 10%);
  }
}
